-- =============================
-- UI + Spawner (robustifi√©)
-- =============================
local verticalOffset = 0.5

local buttonParameters          = {}
buttonParameters.function_owner = self
buttonParameters.width          = 1200
buttonParameters.height         = 300
buttonParameters.position       = { x = -0.2, y = 0.06, z = -verticalOffset }

local CycleIndex = 1
local CycleList = {
  "Night of the Zealot",
  "The Dunwich Legacy",
  "The Path to Carcosa",
  "The Forgotten Age",
  "The Circle Undone",
  "The Dream-Eaters",
  "The Innsmouth Conspiracy",
  "Edge of the Earth",
  "The Scarlet Keys",
  "The Feast of Hemlock Vale",
  "The Drowned City",
  "The Blob That Ate Everything",
  "Carnevale of Horrors",
  "Curse of the Rougarou",
  "Guardians of the Abyss",
  "The Labyrinths of Lunacy",
  "Murder at the Excelsior Hotel",
  "War of the Outer Gods",
  "Cyclopean Foundations",
  "Dark Matter",           
  "Side Scenarios (FM)",
  "Arkham Locations",
  "Unsorted",
}

function onSave()
  return JSON.encode({ CycleIndex, spawnData })
end

local function buildDefaultSpawnData()
  return {
    Name      = "Custom_Tile",
    Nickname  = "",
    Tags      = {"ImageSwapperTile"},
    Transform = { posX=0, posY=0, posZ=0, rotX=0, rotY=0, rotZ=0, scaleX=1, scaleY=1, scaleZ=1 },
    CustomImage = {
      ImageURL           = "",
      ImageSecondaryURL  = "",
      WidthScale         = 1.0,
      CustomTile = { Type = 1, Thickness = 0.1, Stackable = false }
    }
  }
end

function onLoad(savedData)
  if savedData ~= nil and savedData ~= "" then
    local loadedData = JSON.decode(savedData)
    CycleIndex       = loadedData[1] or CycleIndex
    spawnData        = loadedData[2]
  end
  if not spawnData then spawnData = buildDefaultSpawnData() end

  buttonParameters.click_function = "selectCycle"
  buttonParameters.tooltip        = "Select a cycle"
  buttonParameters.label          = CycleList[CycleIndex]
  buttonParameters.font_size      = 90
  self.createButton(buttonParameters)

  buttonParameters.click_function = "showImages"
  buttonParameters.tooltip        = "Clic droit retire l'image"
  buttonParameters.label          = "Affiche les images disponibles"
  buttonParameters.position.z     = buttonParameters.position.z + 2 * verticalOffset
  self.createButton(buttonParameters)
end

function selectCycle(_, color)
  Player[color].showOptionsDialog("Select cycle:", CycleList, CycleIndex, optionCallback)
end

function optionCallback(_, optionIndex)
  CycleIndex = optionIndex
  self.editButton({ index = 0, label = CycleList[CycleIndex] })
end

function showImages(_, _, isRightClick)
  removeImages()
  if isRightClick then return end

  local current = DATA[CycleList[CycleIndex]]
  if not current then
    broadcastToAll("[ImageSwapper] No data for selection: "..tostring(CycleList[CycleIndex]), {1,0.2,0.2})
    return
  end

  local entries = {}
  if current[1] ~= nil then
    entries = current
  else
    for _, sub in pairs(current) do
      if type(sub) == "table" then
        for _, e in ipairs(sub) do table.insert(entries, e) end
      end
    end
  end

  if #entries == 0 then
    broadcastToAll("[ImageSwapper] No images found in this group.", {1,0.6,0.2})
    return
  end

  local pos = self.getPosition()
  local offset = 4.5
  pos.x = pos.x - offset
  local rot = self.getRotation()

  for i, entry in ipairs(entries) do
    spawnData.CustomImage.ImageURL = entry.URL
    spawnData.Nickname             = entry.Name
    spawnData.Tags = spawnData.Tags or {}
    local hasTag = false
    for _, t in ipairs(spawnData.Tags) do if t == "ImageSwapperTile" then hasTag = true break end end
    if not hasTag then table.insert(spawnData.Tags, "ImageSwapperTile") end

    spawnObjectData({ data = spawnData, position = pos, rotation = rot, scale = {1,1,1} })

    if i % 10 == 0 then
      pos.x = pos.x - offset
      pos.z = self.getPosition().z
    else
      pos.z = pos.z - offset
    end
  end
end

function removeImages()
  for _, tile in ipairs(getObjectsWithTag("ImageSwapperTile")) do
    if tile and tile.destruct then tile.destruct() end
  end
end
